document.addEventListener("DOMContentLoaded", function() {
    var mainPlayer = document.getElementById("main-player");
    var playerOptions = document.getElementById("player-options");
    var players = document.querySelectorAll(".player-option");
    var playButton = document.createElement("button");
    var closeButton = document.createElement("div");

    playButton.className = "play-button";
    mainPlayer.appendChild(playButton);

    closeButton.className = "close-btn";
    closeButton.innerHTML = "×";
    closeButton.onclick = function(e) {
        if (e) e.stopPropagation();
        mainPlayer.classList.remove("fixed-player");
        if (window.removeEventListener) {
            window.removeEventListener('scroll', handleScroll);
        } else {
            window.onscroll = null;
        }
    };
    mainPlayer.appendChild(closeButton);

     var fullscreenButton = document.createElement('button');
    fullscreenButton.innerHTML = '⛶';
    fullscreenButton.className = 'fullscreen-button';
    document.getElementsByTagName('head')[0].appendChild(document.createElement('style')).textContent = 
    '.fullscreen-button { position: absolute; font-size: 22px !important;top: 10px; right: 10px; width: 40px; height: 40px; background: #000; color: #fff; display: none; z-index: 9999; }';

    var spinner = document.createElement('div');
    spinner.className = 'spinner';

    var playerUrls = {
        "Premium": {
            "Default": "https://fsvid.lol/embed-e98orf37ckj5.html",
            "VFQ": "https://fsvid.lol/embed-e98orf37ckj5.html",
            "VFF": "",
            "VOSTFR": "https://fsvid.lol/embed-mw23hm16z5mm.html"
        },
        "Dood.Stream": {
            "Default": "https://flixeo.xyz//bigwar/newPlayer.php?id=b86334a5-4c28-48e0-be1c-b95267877d08",
            "VFQ": "https://flixeo.xyz//bigwar/newPlayer.php?id=b86334a5-4c28-48e0-be1c-b95267877d08",
            "VFF": "",
            "VOSTFR": "https://flixeo.xyz//bigwar/newPlayer.php?id=e4a5bb72-cdc1-4739-9578-9b2930307a09"
        },
        "Voe": {
            "Default": "https://flixeo.xyz/voe3/newPlayer.php?id=95032b64-2d93-42ba-a5eb-d48e627612d3",
            "VFQ": "https://flixeo.xyz/voe3/newPlayer.php?id=95032b64-2d93-42ba-a5eb-d48e627612d3",
            "VFF": "",
            "VOSTFR": "https://flixeo.xyz/voe3/newPlayer.php?id=3eb20ee4-ff6c-4752-9320-eb2ec657a629"
        },
        "Uqload": {
            "Default": "https://uqload.net/embed-9jptp86fulet.html",
            "VFQ": "https://uqload.net/embed-9jptp86fulet.html",
            "VFF": "",
            "VOSTFR": "https://uqload.net/embed-c4d65ysm0d7c.html"
        },
        "Netu": {
            "Default": "https://1.multiup.us/player/embed_player.php?vid=6k2cqK6Rbbtv&autoplay=no",
            "VFQ": "https://1.multiup.us/player/embed_player.php?vid=6k2cqK6Rbbtv&autoplay=no",
            "VFF": "https://1.multiup.us/player/embed_player.php?vid=&autoplay=no",
            "VOSTFR": "https://1.multiup.us/player/embed_player.php?vid=S5AohnZUDtsn&autoplay=no"
        },
        "Filmoon": {
            "Default": "https://fr.flixeo.xyz/viper/newplayer.php?id=83vJskdKpDOE9uZn1G1n0",
            "VFQ": "https://fr.flixeo.xyz/viper/newplayer.php?id=83vJskdKpDOE9uZn1G1n0",
            "VFF": "",
            "VOSTFR": "https://fr.flixeo.xyz/viper/newplayer.php?id=2gX7eiluPlwcbkyMobaQW"
        }
    };

    var iframeCache = {};

    function preloadIframe(url) {
        if (!iframeCache[url]) {
            var iframe = document.createElement('iframe');
            iframe.src = url;
            iframe.id = "video-iframe";
            iframe.frameBorder = "0";
            iframe.allowFullscreen = true;
            iframe.style.display = 'none';
            iframeCache[url] = iframe;
        }
        return iframeCache[url];
    }

    function loadPlayer(playerName, version) {
        version = version || "Default";
        var playerUrl = playerUrls[playerName] && playerUrls[playerName][version] || "";
        if (!playerUrl) return;

        for (var i = 0; i < players.length; i++) {
            players[i].classList.remove("active");
        }

        var selectedPlayer = document.querySelector('[data-player="' + playerName + '"]');
        if (selectedPlayer) selectedPlayer.classList.add("active");

        mainPlayer.innerHTML = '';
        spinner.style.display = 'block';
        mainPlayer.appendChild(spinner);

        var iframe = preloadIframe(playerUrl);
        iframe.style.display = 'none';
        mainPlayer.appendChild(iframe);

         mainPlayer.appendChild(fullscreenButton);
        fullscreenButton.style.display = 'block';
        fullscreenButton.onclick = function(evt) {
            if(evt) evt.stopPropagation();
            var iframe = document.getElementById('video-iframe');
            if(iframe.requestFullscreen) iframe.requestFullscreen();
            else if(iframe.mozRequestFullScreen) iframe.mozRequestFullScreen();
            else if(iframe.webkitRequestFullscreen) iframe.webkitRequestFullscreen();
            else if(iframe.msRequestFullscreen) iframe.msRequestFullscreen();
        };

        iframe.onload = function() {
            spinner.style.display = 'none';
            iframe.style.display = 'block';
        };

        setTimeout(function() {
            if (spinner.style.display === 'block') {
                spinner.style.display = 'none';
                iframe.style.display = 'block';
            }
        }, 500);

        mainPlayer.appendChild(closeButton);

        if (window.addEventListener) {
            window.addEventListener("scroll", handleScroll);
        } else {
            window.onscroll = handleScroll;
        }

        playerOptions.style.display = "flex";
        playButton.style.display = "none";
    }

    function isMobile() {
        return window.innerWidth <= 768 || document.documentElement.clientWidth <= 768;
    }

    function toggleDropdown(player) {
        var dropdown = player.querySelector(".version-dropdown");
        var isVisible = dropdown.style.display === "block";

        for (var i = 0; i < players.length; i++) {
            var p = players[i];
            if (p !== player) {
                var otherDropdown = p.querySelector(".version-dropdown");
                if (otherDropdown) {
                    otherDropdown.style.display = "none";
                    p.style.marginBottom = "0";
                }
            }
        }

        dropdown.style.display = isVisible ? "none" : "block";
        if (isMobile()) {
            player.style.marginBottom = isVisible ? "0" : dropdown.offsetHeight + "px";
        }
    }

    function handleScroll() {
        var playerTop = mainPlayer.offsetTop;
        var scrollTop = window.pageYOffset || document.documentElement.scrollTop;
        var scrollThreshold = 300;

        if (scrollTop > playerTop + scrollThreshold) {
            mainPlayer.classList.add("fixed-player");
        } else {
            mainPlayer.classList.remove("fixed-player");
        }
    }

    playButton.onclick = function() {
        var firstPlayer = players[0] ? players[0].getAttribute('data-player') : "Premium";
        loadPlayer(firstPlayer, "Default");
    };

    for (var i = 0; i < players.length; i++) {
        var player = players[i];

        player.onclick = function(e) {
            e = e || window.event;
            if (e.stopPropagation) e.stopPropagation();
            var playerName = this.getAttribute('data-player');

            if (e.target.classList.contains("version-option")) {
                loadPlayer(playerName, e.target.getAttribute('data-version'));
            } else {
                loadPlayer(playerName, "Default");
            }

            if (isMobile()) toggleDropdown(this);
        };

        if (!isMobile()) {
            player.onmouseover = function() {
                var dropdown = this.querySelector(".version-dropdown");
                if (dropdown) dropdown.style.display = "block";
            };

            player.onmouseout = function() {
                var dropdown = this.querySelector(".version-dropdown");
                if (dropdown) dropdown.style.display = "none";
            };
        }
    }

    document.onclick = function(e) {
        e = e || window.event;
        var target = e.target || e.srcElement;

        if (!target.closest || !target.closest(".player-option")) {
            for (var i = 0; i < players.length; i++) {
                var player = players[i];
                var dropdown = player.querySelector(".version-dropdown");
                if (dropdown) {
                    dropdown.style.display = "none";
                    player.style.marginBottom = "0";
                }
            }
        }
    };

    var resizeTimeout;
    window.onresize = function() {
        if (resizeTimeout) clearTimeout(resizeTimeout);
        resizeTimeout = setTimeout(function() {
            if (isMobile()) {
                for (var i = 0; i < players.length; i++) {
                    var player = players[i];
                    var dropdown = player.querySelector(".version-dropdown");
                    if (dropdown) {
                        dropdown.style.display = "none";
                        player.style.marginBottom = "0";
                    }
                }
            }
        }, 250);
    };

    document.onkeydown = function(e) {
        e = e || window.event;
        if (e.key === 'Enter' || e.key === ' ' || e.keyCode === 13 || e.keyCode === 32) {
            if (document.activeElement.classList.contains('player-option')) {
                document.activeElement.click();
            }
        }
    };
});

