     document.addEventListener('keydown', function(event) {
        if (event.key === "Escape") {
            modal.style.display = "none";
            trailer.src = "";
        }
    });
});

document.addEventListener("DOMContentLoaded", function() {
    var elementsPremiumDate = document.getElementsByClassName("premium-date");

    for (var i = 0; i < elementsPremiumDate.length; i++) {
        var dateMembrePremium = elementsPremiumDate[i].textContent.trim();
        var parentElement = elementsPremiumDate[i].closest('.comm-right');

         if (parentElement && parentElement.textContent.includes("BANNI")) {
             if (dateMembrePremium.startsWith("Depuis")) {
                elementsPremiumDate[i].textContent = "BANNI";
            } else {
                elementsPremiumDate[i].textContent = "";  
            }
            continue;  
        }

        var annee = "";
        if (dateMembrePremium.startsWith("Premium Depuis")) {
            annee = dateMembrePremium.split(' ').pop();
        } else if (dateMembrePremium.includes('.')) {
            annee = dateMembrePremium.split('.').pop();
        }

        if (annee !== "") {
            elementsPremiumDate[i].textContent = "Depuis " + annee;
        } else {
             elementsPremiumDate[i].textContent = dateMembrePremium.replace("Depuis ", "").trim();
        }
    }
});

