document.addEventListener("DOMContentLoaded", function() {
    // Sélection de tous les éléments de bouton
    var boutonsRegarderHD = document.querySelectorAll('.btnnew:nth-of-type(1)');
    var boutonsTelechargerHD = document.querySelectorAll('.btnnew:nth-of-type(2)');

    // Fonction de redirection pour Regarder HD
    function redirectToRegarderHD() {
        var title = "Reacher - Saison 3"; // Remplacez "Reacher - Saison 3" par la valeur appropriée si vous avez accès à celle-ci
        var url = "/regarder-le-film.php?voir-film=" + encodeURIComponent(title);
        window.open(url, '_blank');
    }

    // Fonction de redirection pour Télécharger HD
    function redirectToTelechargerHD() {
        var title = "Reacher - Saison 3"; // Remplacez "Reacher - Saison 3" par la valeur appropriée si vous avez accès à celle-ci
        var url = "/telecharger.php?voir-film=" + encodeURIComponent(title);
        window.open(url, '_blank');
    }

    // Ajout d'écouteurs d'événements à chaque occurrence des boutons Regarder HD
    boutonsRegarderHD.forEach(function(bouton) {
        bouton.addEventListener('click', redirectToRegarderHD);
    });

    // Ajout d'écouteurs d'événements à chaque occurrence des boutons Télécharger HD
    boutonsTelechargerHD.forEach(function(bouton) {
        bouton.addEventListener('click', redirectToTelechargerHD);
    });
});

$(document).ready(function(){


 var marquee = $('div.marquee');
  marquee.each(function() {
    var mar = $(this),
      indent = mar.width();
    mar.marquee = function() {
      indent--;
      mar.css('text-indent', indent);
      if (indent < -1 * mar.children('div.marquee-text').width()) {
        indent = mar.width();
      }
    };
    mar.data('interval', setInterval(mar.marquee, 1000 / 60));
    mar.hover(function() {
      clearInterval($(this).data("interval"));
    }, function() {
      $(this).data('interval', setInterval(mar.marquee, 1000 / 60));
    });
  });







