            downloadOptions.style.display = 'none';
        }
    });

    function downloadFile(quality) {
        var originalUrl;

        if (quality === 'haute') {
            originalUrl = "/inscription";
        } else if (quality === 'moyenne') {
            originalUrl = "/inscription";
        } else if (quality === 'basse') {
            originalUrl = "/inscription";
        }

        var newUrl = transformUrl(originalUrl);
        window.open(newUrl, '_blank');
    }

    function transformUrl(url) {
        return url.replace('/embed-', '/d/').replace('.html', '.html');
    }

