document.addEventListener('DOMContentLoaded', (event) => {
    const modal = document.getElementById("modalCustom");
    const btn = document.getElementById("openModalBtnCustom");
    const span = document.getElementsByClassName("close-custom")[0];
    const trailer = document.getElementById("trailerCustom");

     const trailerUrl = 'Rcc9veq0JgM'.trim();

    if (btn) {
        btn.onclick = function(e) {
            e.preventDefault();
            if (trailerUrl) {
                modal.style.display = "block";
                trailer.src = `https://www.youtube.com/embed/${trailerUrl}?autoplay=1`;
            }
