        $targetBox.html('<span class="fa fa-spinner fa-spin"></span>');
        $.post(dle_root + "engine/ajax/custom.php", { castom: $castom }, function(data) {
            $targetBox.html(data);
        });
        $(this).addClass('current').siblings().removeClass('current');
    });

    $(".st-filt").click(function() {
        $("#filter-wrap").fadeToggle(0);
    });

    if ($(window).width() > 1220) {
        $('body').on('click', '.short-in', function() {
            var shortIn = $(this),
                sUrl = shortIn.children('.short-poster').attr('href'),
                sText = shortIn.find('.short-text'),
                winWidth = window.innerWidth,
                sPos = shortIn.offset().left;
            if (sPos > winWidth / 2) {
                shortIn.closest('.short').addClass('pop-left');
            }
            shortIn.closest('.short').addClass('short-active').find('.short-text').fadeIn(200);
            if (shortIn.hasClass('nl')) {
                $.ajax({
                    url: sUrl,
                    beforeSend: function() {},
                    success: function(data) {
                        sText.append($('#s-title', data)).append($('#s-db', data)).append($('#s-desc', data)).append($('#s-list', data));
                        sText.append('<a class="button sl-link icon-l" href="' + sUrl + '"><span class="fa fa-play-circle-o"></span>' + titlesArr[0] + '</a>');
                        shortIn.removeClass('nl');
                    },
                    error: function() {
                        console.log('error');
                    }
                });
            }
        });
    }

    $('.tabs-box').each(function() {
        $(this).find('.tabs-sel span:first').addClass('current');
        $(this).find('.tabs-b:first').addClass('visible');
    });
    $('.tabs-sel').delegate('span:not(.current)', 'click', function() {
        $(this).addClass('current').siblings().removeClass('current')
            .parents('.tabs-box').find('.tabs-b').hide().eq($(this).index()).fadeIn(400);
    });

    $('.frate').each(function() {
        var a = $(this),
            aVotes = a.children('.fr-votes'),
            b = parseInt(aVotes.find('.ratingtypeplusminus').text()),
            c = parseInt(aVotes.find('span[id]:last').text());
        if (c >= b && c > 0) {
            var m = (c - b) / 2,
                p = c - m,
                pTen = (Math.round((c - (c - b) / 2) / c * 100)) / 10,
                mTen = 10 - pTen;
        } else {
            var m = 0,
                p = 0,
                pTen = 0,
                mTen = 0;
        }
        a.find('.ps').append('<span class="psc">' + p + '</span>');
        a.find('.ms').append('<span class="msc">' + m + '</span>');
        a.find('.fr-common').append('<div>' + pTen + '<div>' + titlesArr[1] + '</div></div>');
        a.find('.fr-plus').circleProgress({
            size: 80.0,
            startAngle: -1.5,
            lineCap: 'round',
            value: pTen / 10,
            emptyFill: '#1e1e1e',
            fill: { gradient: ['#2dc225', '#33a42d'] }
        });
        a.find('.fr-minus').circleProgress({
            size: 80.0,
            startAngle: -1.5,
            lineCap: 'round',
            value: mTen / 10,
            emptyFill: '#1e1e1e',
            fill: { gradient: ['#f4470b', '#dd4d1b'] }
        });
    });

    $('body').append('<div class="close-overlay" id="close-overlay"></div><div class="side-panel" id="side-panel"></div>');
    $('#nav-menu').each(function() {
        $(this).clone().appendTo('#side-panel');
    });
    if ($(window).width() < 560) {
        $('#hd-right').prependTo('#side-panel');
    }
    $(".btn-menu").click(function() {
        $('#side-panel').addClass('active');
        $("#close-overlay").fadeIn(200);
    });
    $(".close-overlay").click(function() {
        $('#side-panel').removeClass('active');
        $('#close-overlay').fadeOut(200);
    });

    $('.full-text table').each(function() {
        $(this).wrap("<div class='table-resp' />");
    });

    $("#login-box").dialog({
        autoOpen: false,
        modal: true,
        show: 'fade',
        hide: 'fade',
        width: 320
    });

    $('.show-login').click(function() {
        $('#login-box').dialog('open');
    });

    $('#show-login, #ac-av').html($('#lb-ava').html());

    $(".add-comm-btn").click(function() {
        $("#add-comm-form").slideToggle(200);
    });
    $(".reply").click(function() {
        $("#add-comm-form").slideDown(200);
    });
    $('body').on('click', '.ac-textarea textarea, .fr-wrapper', function() {
        $('.add-comm-form').addClass('active').find('.ac-protect').slideDown(400);
    });
    $('.lb-soc a, .ac-soc a').on('click', function() {
        var href = $(this).attr('href');
        var width = 820;
        var height = 420;
        var left = (screen.width - width) / 2;
        var top = (screen.height - height) / 2 - 100;

        auth_window = window.open(href, 'auth_window', "width=" + width + ",height=" + height + ",top=" + top + "menubar=no,resizable=no,scrollbars=no,status=no,toolbar=no");
        return false;
    });
    $('#dle-content > #dle-ajax-comments').appendTo($('#full-comms'));

    $('body').append('<div id="gotop"><span class="fa fa-chevron-up"></span></div>');
    var $gotop = $('#gotop');
    $(window).scroll(function() {
        if ($(this).scrollTop() > 300) { $gotop.fadeIn(200); } else { $gotop.fadeOut(200); }
    });
    $gotop.click(function() {
        $('html, body').animate({ scrollTop: 0 }, 'slow');
    });

});





	function doRateLD( rate, id ) {
		ShowLoading('');
		$.get(dle_root + "engine/ajax/rating.php", { go_rate: rate, news_id: id, skin: dle_skin, user_hash: dle_login_hash }, function(data){
			HideLoading('');
			if ( data.success ) {
				var rating = data.rating;
				rating = rating.replace(/&lt;/g, "<");
				rating = rating.replace(/&gt;/g, ">");
				rating = rating.replace(/&amp;/g, "&");
				$("#ratig-layer-" + id).html(rating);
				$("#vote-num-id-" + id).html(data.votenum);
				var rt = parseInt($(rating).text()),
					m = (data.votenum - rt)/2,
					p = data.votenum - m,
					pTen = (Math.round((data.votenum - (data.votenum - rt)/2)/data.votenum*100))/10,
					mTen = 10 - pTen,
					fRate = $("#frate-" + id);
				fRate.find('.psc').text(p);
				fRate.find('.msc').text(m);
				fRate.find('.fr-common').html('<div>'+pTen+'<div>'+titlesArr[1]+'</div></div>');
				fRate.find('.fr-plus').circleProgress({
					value: pTen/10
				});
				fRate.find('.fr-minus').circleProgress({
					value: mTen/10
				});
			} else if (data.error) {DLEalert ( data.errorinfo, dle_info );}
		}, "json");
	};
	
