function openPopup(type) {
            let content = "";
            if (type === 'donation') {
                content = `
                    <h2>Soutenez-nous avec un don BTC</h2>
                    <img src='https://i.imgur.com/wy5YZ19.jpeg' alt='QR Code'>
                    <p class='btc-key'>Adresse BTC : <br><strong>bc1q0lpl5vmdp6vf3n5s3k7y5lszvsmsw35k4q4fkv</strong></p>
                    <p class='help-link' onclick='openPopup("help")'>Pas familiarisé avec le BTC ?</p>
                `;
            } else if (type === 'help') {
                content = `
                    <div class='help-content'>
                        <h2>Avantages du Bitcoin</h2>
                        <ul>
                            <li><strong>Sécurisé</strong> - Le Bitcoin est une monnaie numérique sécurisée. Contrairement à PayPal ou à une carte bancaire, il n’est pas nécessaire de saisir des informations bancaires ni de fournir une adresse e-mail. Il suffit simplement de scanner le QR code avec votre téléphone, et c'est terminé !</li>
                            <li><strong>Coinbase</strong> - Pour commencer à utiliser le BTC, nous vous conseillons l'application Coinbase, car elle est simple d'utilisation. Il vous suffit de télécharger Coinbase depuis votre store pour recevoir et envoyer du BTC facilement.</li>
                            <li><strong>Faire un don</strong> - Une fois l'application installée, connectez-vous puis scannez simplement notre QR Code pour envoyer un don.</li>
                        </ul>
                        <p class='help-link' onclick='openPopup("donation")'>Faire un don</p>
                    </div>
                `;
            }
            document.getElementById("popup-content").innerHTML = content;
            document.getElementById("popup").style.display = "block";
            document.getElementById("overlay").style.display = "block";
        }

        function closePopup() {
            document.getElementById("popup").style.display = "none";
            document.getElementById("overlay").style.display = "none";
        }

